mapper.select:
select
id, name, content, parent_id, time, from_name, gmt_create
from comment


        and id = #{id}


        and name = #{name}


        and content = #{content}


        and parent_id = #{parentId}


        and time = #{time}


        and from_name = #{fromName}


        and gmt_create = #{gmtCreate}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from comment


        and id = #{id}


        and name = #{name}


        and content = #{content}


        and parent_id = #{parentId}


        and time = #{time}


        and from_name = #{fromName}


        and gmt_create = #{gmtCreate}
